mapper.delete:

DELETE FROM booking_requests
WHERE br_no = #{brNo}
AND deposit_status = 0

mapper.update:
UPDATE booking_requests SET approval_status = 2
WHERE deposit_status = 0
AND fr_no = #{frNo}

UPDATE facility_rental
SET buy_username = (
    SELECT username
    FROM booking_requests
    WHERE br_no = #{brNo}
), confirmed = 1
WHERE fr_no = (
    SELECT fr_no
    FROM booking_requests
    WHERE br_no = #{brNo}
)
